import java.io.FileOutputStream; import java.security.KeyPair; import java.security.KeyPairGenerator; import java.util.Base64; public class KeyGen { public static void main(String[] args) throws Exception { KeyPairGenerator kpg = KeyPairGenerator.getInstance("RSA"); kpg.initialize(2048); KeyPair kp = kpg.generateKeyPair(); String priv = Base64.getEncoder().encodeToString(kp.getPrivate().getEncoded()); String pub = Base64.getEncoder().encodeToString(kp.getPublic().getEncoded()); try (FileOutputStream fos = new FileOutputStream("private.pem")) { fos.write(priv.getBytes()); } try (FileOutputStream fos = new FileOutputStream("public.pem")) { fos.write(pub.getBytes()); } System.out.println("RSA Keys Generated"); } }
